public class 변수설정 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * 		국어점수, 영어점수, 수학점수
		 * 		  90	  85	  80
		 */
		
		int kor=90;
		int eng=85;
		int math=80;
		
		//점수출력
		System.out.println("국어:"+kor);      // +의 두가지 기능 => 문자열결합 , 산술연산
		System.out.println("영어:"+eng);
		System.out.println("수학:"+math);

	}

}
